mapper.select:
SELECT com_id, name, address, password, tel, description, logo_img, salt
FROM company

	WHERE com_id = #{comId}

mapper.update:
UPDATE company SET name=#{name}, address=#{address}, password= #{password},
					tel=#{tel},description = #{description},logo_img =#{logoImg},salt =#{salt}
WHERE com_id = #{comId}
