mapper.insert:
INSERT INTO
repeat_date(weekday, time, stu_id)
VALUES(#{weekday}, #{time}, #{stuId});

mapper.select:
SELECT repeat_id,weekday,time,stu_id
FROM repeat_date
WHERE stu_id = #{value};

mapper.update:
UPDATE repeat_date
SET weekday = #{weekday}, time=#{time}
WHERE stu_id = #{stuId}
and weekday = #{weekday}
