mapper.select:
AND t.tag_name = #{searchTag}
AND c.category_id = #{categoryId}
AND r.weekday = #{weekDate}
AND s.stu_id != All (SELECT stu_id FROM exception_date WHERE #{selectedDate} >= start_date AND end_date >= #{selectedDate} )
AND f.address Like "%${address1}%" AND f.address Like "%${address2}%"
AND f.size >= #{minSize}
AND #{maxSize} >= f.size
AND f.unit_price >= #{minUnitPrice}
AND #{maxUnitPrice} >= f.unit_price
AND f.max_capacity >= #{capacity}
AND
s.name Like '%${search}%' OR c.category_name Like '%${search}%' OR s.description Like '%${search}%' OR t.tag_name Like '%${search}%' OR f.address Like '%${search}%' OR com.name Like '%${search}%'
GROUP BY s.stu_id
ORDER BY res.count DESC
ORDER BY f.unit_price DESC
ORDER BY f.unit_price ASC
ORDER BY rv.avg DESC
limit #{page} , 5

SELECT book_id, stu_id
FROM bookmark
WHERE cust_id = #{custId}
AND stu_id = #{stuId}
